	sortOrder = ['comm', 'edu', 'health', 'indep', 'policy'];
for (var i = 0; i < sortOrder.length; i++)
	category_ordering[sortOrder[i]] = i;
data_dictionary.sort( function(a, b) {
	return (category_ordering[a.variable.split("_")[0]] - category_ordering[b.variable.split("_")[0]]);
});
